function showPopup(map, coord, layer, counter) {
    if (typeof layer == "undefined" || layer == "") {
        layer = "markers";
    }
    var features = map.queryRenderedFeatures(coord, { layers: [layer] });
    if (!features.length) {
        return;
    }
    if (typeof counter == "undefined" || counter == "") {
        counter = 0;
    }

    var feature = features[0];
    var popup = null;
    if (layer == "markers") {
        popup = new mapboxgl.Popup({
            anchor: "bottom",
            offset: {'bottom': [0, -45]},
            closeButton: false
        });
    } else {
        popup = new mapboxgl.Popup({
            anchor: "top",
            offset: {'top': [0, 45]}
        });
    }
    popup
        .setLngLat(feature.geometry.coordinates)
        .setHTML(feature.properties.description)
        .addTo(map);
    map.flyTo({
        center: feature.geometry.coordinates,
        zoom: 6
    });
    $(".mapboxgl-popup").removeClass("visible");
    var isAnimating = setInterval(function() {
        if (map.isMoving() == false) {
            $(popup._container).addClass("visible");
            clearInterval(isAnimating);
        }
    }, 10);
}
function getTimeRemaining(endtime) {
    var t = Date.parse(endtime) - Date.parse(new Date());
    var seconds = Math.floor( (t/1000) % 60 );
    var minutes = Math.floor( (t/1000/60) % 60 );
    var hours = Math.floor( (t/(1000*60*60)) % 24 );
    var days = Math.floor( t/(1000*60*60*24) );
    return {
        'total': t,
        'days': days,
        'hours': hours,
        'minutes': minutes,
        'seconds': seconds
    };
}

function initializeClock(id, endtime) {
    var clock = document.getElementById(id);
    var hoursSpan = clock.querySelector('.timer__hours');
    var minutesSpan = clock.querySelector('.timer__mins');
    var secondsSpan = clock.querySelector('.timer__secs');

    function updateClock() {
        var t = getTimeRemaining(endtime);

        hoursSpan.innerHTML = ('0' + (t.hours + 24*t.days)).slice(-2);
        minutesSpan.innerHTML = ('0' + t.minutes).slice(-2);
        secondsSpan.innerHTML = ('0' + t.seconds).slice(-2);

        if (t.total <= 0) {
            clearInterval(timeinterval);
        }
    }

    updateClock();
    var timeinterval = setInterval(updateClock, 1000);
}

$(document).ready(function() {
    $(".timer").each(function() {
        var deadline = ($(this)[0].hasAttribute("data-countdown")) ? $(this).attr("data-countdown") : "";
        if (deadline == "") {
            deadline = new Date(Date.parse(new Date()) + 2 * 24 * 60 * 60 * 1000);
        } else {
            deadline = new Date(deadline);
        }
        initializeClock($(this).attr('id'), deadline);
    });
});
